  const html = await httpGet(this.api + '/?page=' + page);
  const $ = cheerio.load(html);
  const list = [];
  $('.video-list li, .item').each((_, el) => {
    const id = $(el).find('a').attr('href')?.split('/').pop() || $(el).data('id');
    const cover = $(el).find('img').attr('src') || '';
    const title = $(el).find('h3, .title').text().trim();
    const remark = $(el).find('.type, .year').text().trim();
    const desc = $(el).find('.desc').text().trim();
    if (id && title) list.push({ id, cover, title, remark, desc, playlist: [] });
  });
  return list;
}

js.search:
async getSearch(keyword, page = 1) {
  const url = this.api + '/search?q=' + encodeURIComponent(keyword) + '&page=' + page;
  const html = await httpGet(url);
  const $ = cheerio.load(html);
  const results = [];
  $('.search-item, .result').each((_, el) => {
    const id = $(el).find('a').attr('href')?.split('/').pop();
    const cover = $(el).find('img').attr('src') || '';
    const title = $(el).find('h3').text().trim();
    const remark = $(el).find('.type').text().trim();
    const desc = $(el).find('.intro').text().trim();
    if (id && title) results.push({ id, cover, title, remark, desc, playlist: [] });
  });
  return results;
}

js.detail:
async getDetail(id) {
  const url = this.api + '/detail/' + id;
  const html = await httpGet(url);
  const $ = cheerio.load(html);
  const playlist = [];
  $('.playlist a, .episode li').each((_, el) => {
    const text = $(el).text().trim();
    let href = $(el).attr('href');
    if (href.startsWith('http')) href = '$iframe_url' + href;  // 标记为 iframe
    if (text) playlist.push({ text, id: href });
  });
  return {
    id,
    cover: $('.poster img').attr('src') || '',
    title: $('.title').text().trim(),
    remark: $('.info .type').text().trim(),
    desc: $('.desc').text().trim(),
    playlist
  };
}

js.parseIframe:
async parseIframe(url) {
  const html = await httpGet(url);
  const $ = cheerio.load(html);
  let videoSrc = $('video source').attr('src') || $('.player iframe').attr('src') || $('embed').attr('src');
  if (!videoSrc) {
    // 尝试 base64 解码常见加密
    const scripts = $('script').html();
    const match = scripts.match(/eval\((.*?)\)/);
    if (match) videoSrc = eval(decodeURIComponent(match[1]));
  }
  return videoSrc || '';
}

js.category:
async getCategory() {
  const html = await httpGet(this.api + '/');
  const $ = cheerio.load(html);
  const cats = [];
  $('ul.category li a, .menu a').each((_, el) => {
    const text = $(el).text().trim();
    const href = $(el).attr('href');
    if (text && href) {
      const id = href.match(/cat=(\d+)/)?.[1] || href.split('/').pop();
      cats.push({ text, id: id || text });
    }
  });
  return cats.length ? cats : [{ text: '全部', id: 1 }];
}

